// --- Markdown -> Plain text ---
function mdToPlain(input) {
  let s = String(input ?? "");

  // normalise newlines + "\n"
  s = s.replace(/\r\n?/g, "\n");
  s = s.replace(/\\n/g, "\n");

  // headings
  s = s.replace(/^\s{0,3}#{1,6}\s*/gm, "");

  // blockquote
  s = s.replace(/^\s{0,3}>\s?/gm, "");

  // ordered list -> 1) ...
  s = s.replace(/^\s{0,3}(\d+)\.\s+/gm, (_, n) => `${n}) `);

  // bullet list -> ‚Ä¢ ...
  s = s.replace(/^\s{0,3}[-*+]\s+/gm, "‚Ä¢ ");

  // images ![alt](url) -> alt
  s = s.replace(/!\[([^\]]*)\]\(([^)]+)\)/g, "$1");

  // links [text](url) -> text
  s = s.replace(/\[([^\]]+)\]\(([^)]+)\)/g, "$1");

  // bold/italic
  s = s.replace(/(\*\*|__)(.*?)\1/g, "$2");
  s = s.replace(/(\*|_)(.*?)\1/g, "$2");

  // code blocks & inline code
  s = s.replace(/```[\s\S]*?```/g, m => m.replace(/```/g, "").trim());
  s = s.replace(/`([^`]+)`/g, "$1");

  // hr
  s = s.replace(/^\s*[-*_]{3,}\s*$/gm, "");

  // trim line-end spaces + collapse blank lines
  s = s.replace(/[ \t]+$/gm, "");
  s = s.replace(/\n{3,}/g, "\n\n");

  return s.trim();
}

